#include<iostream>
using namespace std;
int main(){
int s ;
cout<<"Enter the size of the array"<<endl;
cin>>s;
int arr[s];
s=sizeof(arr)/sizeof(arr[0]);
cout<<"Enter the elements of the array"<<endl;
for(int i=0;i<s;i++){
    cin>>arr[i];
}
for(int i=0;i<s;i++){
    cout<<arr[i]<<"\t";
}
cout<<"\noutput of bubble sort\n";
int c;
for(int i=0;i<s;i++){
    c=0;
    for(int j=0;j<s-i-1;j++){
        int tmp;
        if(arr[j]>arr[j+1]){
            tmp=arr[j];
            arr[j]=arr[j+1];
            arr[j+1]=tmp;
            c++;
        }
    }
    if(c==0){
        break;
    }
}
for(int i=0;i<s;i++){
    cout<<arr[i]<<"\t";
}
}